# filename: draw_line.py
from PIL import Image, ImageDraw

# Create a new image with white background
img = Image.new('RGB', (200, 200), color = 'white')

# Initialize the drawing context with the image as background
draw = ImageDraw.Draw(img)

# Draw a black line from the top-left corner to the bottom-right corner
draw.line((0, 0) + img.size, fill='black')

# Save the image to a file
img.save('line_image.png')

# Display the image
img.show()