# filename: draw_line_turtle.py
import turtle

# Set up the screen
wn = turtle.Screen()
wn.title("Line Drawing")

# Create a turtle
line_drawer = turtle.Turtle()

# Draw a line from (0, 0) to (100, 0)
line_drawer.penup()
line_drawer.goto(0, 0)
line_drawer.pendown()
line_drawer.goto(100, 0)

# Close the turtle graphics window when clicked
wn.exitonclick()